using UnityEngine;public class BattleUnit : MonoBehaviour{	static private int _id = 1; 	private string Kind;	private int MaxHp;	protected int currentHP;	private float Defence;	private int Attack;	protected float Speed;	[HideInInspector]	public int ID;	public void Init(string kind,		int hp,		float defence,		int attack,		float speed		)	{		ID = _id;		_id++;		Kind = kind;		currentHP = hp;		MaxHp = hp;		Defence = defence;		Attack = attack;		Speed = speed * 0.001f;	}	public virtual int GetAttack ()	{		return Attack;	}	public virtual void GetDamage(int damage)	{		var d = Defence * (float)damage;		currentHP -= Mathf.CeilToInt(d);	}	public bool IsDead ()	{		return currentHP <= 0;	}}