using UnityEngine;public class TankControls : MonoBehaviour{	private Tank tank; 	public void Init (Tank tank)	{        this.tank = tank;	}    void Update()    {        if (tank == null)            return;        if (Input.GetKey(KeyCode.UpArrow))        {            tank.Move(true);        }        else if (Input.GetKey(KeyCode.DownArrow))        {            tank.Move(false);        }        if (Input.GetKey(KeyCode.LeftArrow))        {            tank.Rotate(false);        }        else if (Input.GetKey(KeyCode.RightArrow))        {            tank.Rotate(true);        }        if (Input.GetKey(KeyCode.X))        {            tank.Fire();        }        if (Input.GetKey(KeyCode.Q))		{            tank.ChangeCannon(0);		} else if (Input.GetKey(KeyCode.W))		{            tank.ChangeCannon(1);        }    }}