using UnityEngine;using TMPro;public class TankPanel: MonoBehaviour{	[SerializeField]	private TextMeshProUGUI hp;	private int currentHP;	private int maxHP;	public void SetCurrentHP(int currentHp)	{		this.currentHP = currentHp;		RefreshHP();	}	public  void SetMaxHP (int maxHP)	{		this.maxHP = maxHP;		RefreshHP();	}	private void RefreshHP ()	{		hp.text = $"HP: {currentHP}/{maxHP}";	}}