using System.Linq;public class Dict{	public MonsterInfo[] MonsterInfos;	public LevelInfo[] LevelInfos;	public TankInfo[] TankInfos;	public Dict(MonsterInfo[] monsterInfos,		LevelInfo[] levelInfos,		TankInfo[] tankInfos)	{		MonsterInfos = monsterInfos;		LevelInfos = levelInfos;		TankInfos = tankInfos;	}	public MonsterInfo GetMonster(string kind)	{		return MonsterInfos.FirstOrDefault(m => m.Kind == kind);	}	public LevelInfo GetLevel(string kind)	{		return LevelInfos.FirstOrDefault(l => l.Kind == kind);	}	public TankInfo GetTank(string kind)	{		return TankInfos.FirstOrDefault(t => t.Kind == kind);	}	public static Dict TestDict()	{		var monsters = new MonsterInfo[]		{			new MonsterInfo("Rabbit", 10, 1f, 2, 5),			new MonsterInfo("Ghost", 5, 0.4f, 4, 7f)		};		var levelInfos = new LevelInfo[]		{			new LevelInfo("level1", 10, new string[] { "Rabbit", "Ghost" }, 2f )		};		var cannon1 = new CannonInfo("cannon1", 5, 0.5f, 100f);		var cannon2 = new CannonInfo("cannon2", 10, 1f, 50f);		var tanks = new TankInfo[]		{			new TankInfo("tank1", 100, 0.9f, 10f, new CannonInfo[] { cannon1, cannon2  })		};		return new Dict(monsters, levelInfos, tanks);	}}