public class MonsterInfo{	public readonly string Kind;	public readonly string MonsterView;	public readonly int HP;	public readonly float Defence;	public readonly int Attack;	public readonly float Speed;	public MonsterInfo(string kind,		int hp,		float defence,		int attack,		float speed)	{		Kind = kind;		HP = hp;		MonsterView = kind;		Defence = defence;		Attack = attack;		Speed = speed;	}}