public class TankInfo{	public readonly string Kind;	public readonly string TankView;	public readonly int HP;	public readonly float Defence;	public readonly float Speed;	public readonly CannonInfo[] Cannons;	public TankInfo(string kind,		int hp,		float defence,		float speed,		CannonInfo[] cannons)	{		Kind = kind;		HP = hp;		TankView = kind;		Defence = defence;		Speed = speed;		Cannons = cannons;	}}